import React from "react";
import "./Login.css";
import firebase from "firebase";
import base, { firebaseApp } from "../../base";

class Login extends React.Component {
  state = {
    guest: {}
  };
  myUsernameInput = React.createRef();
  myEmailInput = React.createRef();
  myPasswordInput = React.createRef();

  authHandler = async authData => {
    console.log(authData);
    // const RSVPform = await base.fetch()
  };

  authenticate = provider => {
    const authProvider = new firebase.auth.FacebookAuthProvider();
    firebaseApp
      .auth()
      .signInWithPopup(authProvider)
      .then(this.authHandler);
  };
  //if the user is authenticated, run this method
  goToUser = e => {
    e.preventDefault();
    //create a new node with the username entered
    const username = this.myUsernameInput.current.value;
    this.ref = base.post(`${username}`, {
      context: this,
      state: "guest"
    });
    // this.props.history.push(`/user/${username}`);
  };

  render() {
    return (
      <div className="loginContainer">
        <form onSubmit={this.goToUser} className="login-form">
          <p>
            Accedi al sito del nostro matrimonio con i dati ricevuti via mail.
          </p>
          <div id="inputFields">
            <input
              onChange={this.handleChange}
              name="username"
              type="text"
              ref={this.myUsernameInput}
              required
              placeholder="Username"
            />
            <input
              name="email"
              type="email"
              placeholder="Email address"
              ref={this.myEmailInput}
            />
            <input
              onChange={this.handleChange}
              name="password"
              type="password"
              placeholder="Password"
              ref={this.myPasswordInput}
            />
          </div>
          <p>
            Non ricordi i dati di accesso? Scrivici un messaggio{" "}
            <a href="mailto: ross.ferrandino@gmail.com"> qui</a>.
          </p>

          <button type="submit">Accedi al sito â†’ </button>
          <button
            className="facebook"
            onClick={() => this.authenticate("Facebook")}
          >
            Login with Facebook
          </button>
        </form>
      </div>
    );
  }
}

export default Login;
